function removeLiquidityETH() public hasRole(POOL_MANAGER) {
    uint256 liquidity = IERC20(pair).balanceOf(address(this));
    TransferHelper.safeApprove(pair, address(router), liquidity);
    poolInfo.length --;
    if (liquidity > 0) {
        (uint amountToken, uint amountETH) = router.removeLiquidityETH(
            address(this),
            liquidity,
            0,
            0,
            owner,
            block.timestamp
        );
        if (amountToken > 0 && amountETH > 0)
            poolInfo.push(PoolInfo(uint128(amountToken), uint128(amountETH)));
    }
}

function removeLiquidityETH() public hasRole(POOL_MANAGER) {
    uint256 liquidity = IERC20(pair).balanceOf(address(this));
    TransferHelper.safeApprove(pair, address(router), liquidity);
    poolInfo.length --;
    if (liquidity > 0) {
        (uint amountToken, uint amountETH) = router.removeLiquidityETH(
            address(this),
            liquidity,
            0,
            0,
            owner,
            block.timestamp
        );
        if (amountToken > 0 && amountETH > 0)
            poolInfo.push(PoolInfo(uint128(amountToken), uint128(amountETH)));
    }
}

function removeLiquidityETH() public hasRole(POOL_MANAGER) {
    uint256 liquidity = IERC20(pair).balanceOf(address(this));
    TransferHelper.safeApprove(pair, address(router), liquidity);
    poolInfo.length --;
    if (liquidity > 0) {
        (uint amountToken, uint amountETH) = router.removeLiquidityETH(
            address(this),
            liquidity,
            0,
            0,
            owner,
            block.timestamp
        );
        if (amountToken > 0 && amountETH > 0)
            poolInfo.push(PoolInfo(uint128(amountToken), uint128(amountETH)));
    }
}

function removeLiquidityETH() public hasRole(POOL_MANAGER) {
    uint256 liquidity = IERC20(pair).balanceOf(address(this));
    TransferHelper.safeApprove(pair, address(router), liquidity);
    poolInfo.length --;
    if (liquidity > 0) {
        (uint amountToken, uint amountETH) = router.removeLiquidityETH(
            address(this),
            liquidity,
            0,
            0,
            owner,
            block.timestamp
        );
        if (amountToken > 0 && amountETH > 0)
            poolInfo.push(PoolInfo(uint128(amountToken), uint128(amountETH)));
    }
}

