    LockInfo storage info = lockInfos[user];
    require(info.lockAmount == 0); // Make sure the lock is not set
    lockInfos[user] = LockInfo(initialLockAmount, lockAmount, releaseStart, releaseInterval, 0, releasePeriods);
}

