function devFee(uint256 amount) public pure returns(uint256){
    return SafeMath.div(SafeMath.mul(amount,5),100);
}

function devFee(uint256 amount) public pure returns(uint256){
    return SafeMath.div(SafeMath.mul(amount,5),100);
}

