        TradeParams({
            sizeDelta: sizeDelta,
            price: price,
            takerFee: _takerFee(marketKey),
            makerFee: _makerFee(marketKey),
            trackingCode: bytes32(0)
        });
    return (_orderFee(params, dynamicFeeRate), isInvalid || tooVolatile);
}

