function acceptAll() internal {
    address[] memory contracts = contractsRequiringOwnership();
    for (uint i = 0; i < contracts.length; i++) {
        Owned(contracts[i]).acceptOwnership();
    }
}

function acceptAll() internal {
    address[] memory contracts = contractsRequiringOwnership();
    for (uint i = 0; i < contracts.length; i++) {
        Owned(contracts[i]).acceptOwnership();
    }
}

