function _setParameter(
    bytes32 _marketKey,
    bytes32 key,
    uint value
) internal {
    _flexibleStorage().setUIntValue(SETTING_CONTRACT_NAME, keccak256(abi.encodePacked(_marketKey, key)), value);
    emit ParameterUpdated(_marketKey, key, value);
}

function _setParameter(
    bytes32 _marketKey,
    bytes32 key,
    uint value
) internal {
    _flexibleStorage().setUIntValue(SETTING_CONTRACT_NAME, keccak256(abi.encodePacked(_marketKey, key)), value);
    emit ParameterUpdated(_marketKey, key, value);
}

