function getVestingTime(address account, uint index) public view returns (uint) {
    return getVestingScheduleEntry(account, index)[TIME_INDEX];
}

function getVestingTime(address account, uint index) public view returns (uint) {
    return getVestingScheduleEntry(account, index)[TIME_INDEX];
}

